package lesson2;

public class If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 28;
		int b = 3;

		if (a > b) {
			System.out.println("a больше b");
		} else if (a < b) {
			System.out.println("a меньше b");
		} else {
			System.out.println("a равно b");
		}
	}

}
